mapper.select:
SELECT	*,
		(SELECT valueName FROM data_dictionary d WHERE  a.flatformId=d.valueId AND d.typeCode='APP_FLATFORM') AS flatformName,
		(SELECT devName FROM dev_user WHERE id = a.devId) AS devName,			
		(SELECT categoryName FROM app_category c WHERE  c.id=a.categoryLevel1) AS categoryLevel1Name,
		(SELECT categoryName FROM app_category c WHERE  c.id=a.categoryLevel2) AS categoryLevel2Name,
		(SELECT categoryName FROM app_category c WHERE  c.id=a.categoryLevel3) AS categoryLevel3Name,
		(SELECT valueName FROM data_dictionary d WHERE  a.status=d.valueId AND d.typeCode='APP_STATUS') AS statusName,
		(SELECT v.versionNo FROM app_version v WHERE v.id=a.versionId ) AS versionNo
FROM app_info a 

	
		and a.softwareName like CONCAT ('%',#{softwareName},'%')
	
	
		and a.status=#{status}
	
	
		and a.categoryLevel1 = #{categoryLevel1}
	
	
		and a.categoryLevel2 = #{categoryLevel2}
	
	
		and a.categoryLevel3 = #{categoryLevel3}
	
	
		and a.flatformId = #{flatformId}
	
	
		and a.devId = #{devId}
	

ORDER BY creationDate LIMIT #{pageInex},#{pageSize}

SELECT COUNT(*) FROM app_info

	
		and softwareName like CONCAT ('%',#{softwareName},'%')
	
	
		and status=#{status}
	
	
		and categoryLevel1 = #{categoryLevel1}
	
	
		and categoryLevel2 = #{categoryLevel2}
	
	
		and categoryLevel3 = #{categoryLevel3}
	
	
		and flatformId = #{flatformId}
	
	
		and devId = #{devId}

SELECT *, (SELECT valueName FROM data_dictionary d WHERE a.flatformId=d.valueId AND d.typeCode='APP_FLATFORM') AS flatformName, (SELECT devName FROM dev_user WHERE id = a.devId) AS devName, (SELECT categoryName FROM app_category c WHERE c.id=a.categoryLevel1) AS categoryLevel1Name, (SELECT categoryName FROM app_category c WHERE c.id=a.categoryLevel2) AS categoryLevel2Name, (SELECT categoryName FROM app_category c WHERE c.id=a.categoryLevel3) AS categoryLevel3Name, (SELECT valueName FROM data_dictionary d WHERE a.status=d.valueId AND d.typeCode='APP_STATUS') AS statusName, (SELECT v.versionNo FROM app_version v WHERE v.id=a.versionId ) AS versionNo FROM app_info a
a.id = #{id}
a.APKName = #{APKName}

mapper.update:
update app_info
softwareName=#{softwareName}, supportROM=#{supportROM}, interfaceLanguage=#{interfaceLanguage}, updateDate=#{updateDate}, softwareSize=#{softwareSize}, appInfo=#{appInfo}, status=#{status}, onSaleDate=#{onSaleDate}, offSaleDate=#{offSaleDate}, categoryLevel1=#{categoryLevel1}, categoryLevel2=#{categoryLevel2}, categoryLevel3=#{categoryLevel3}, downloads=#{downloads}, flatformId=#{flatformId}, logoPicPath=#{logoPicPath}, logoLocPath=#{logoLocPath}, modifyBy=#{modifyBy}, modifyDate=#{modifyDate},
where id=#{id}
